import React from "react";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import stock_img from '../images/landscape.jpg';
import ProjectBox from '../components/ProjectBox';
import '../components/ProjectSlider.css';


function Arrowprev(props) {
    const { onClick } = props;
    return (
        <i onClick={onClick} className="fa-solid fa-less-than fa-xl prev_btn"></i>
    );
}

function Arrownext(props) {
    const { onClick } = props;
    return (
        <i onClick={onClick} className="fa-solid fa-greater-than fa-xl next_btn"></i>
    );
}

export default function App(){

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        prevArrow: <Arrowprev />,
        nextArrow: <Arrownext />,
        responsive: [
            {
              breakpoint: 1400, // (desktop)
              settings: {
                slidesToShow: 3,
                slidesToScroll: 1,
              },
            },
            {
              breakpoint: 1220, // (tablet)
              settings: {
                slidesToShow: 2,
                slidesToScroll: 1,
              },
            },
            {
              breakpoint: 820, // (mobile)
              settings: {
                slidesToShow: 1,
                slidesToScroll: 1,
              },
            },
          ],
      };

    return(
    <div className="main-container">     
        <div className="carousel-container">
            <Slider {...settings}>
            <ProjectBox img={stock_img}/>
            <ProjectBox img={stock_img}/>
            <ProjectBox img={stock_img}/>
            <ProjectBox img={stock_img}/>
            <ProjectBox img={stock_img}/>
            <ProjectBox img={stock_img}/>
            </Slider>
        </div>
    </div>
    );
}
