import React, { useState } from "react";
import './Searchbar.module.css';

export default function Searchbar(){

    const [isclicked, setisClicked] = useState(false);

    function dropdown(){
      setisClicked(!isclicked);
    }

    return <div className=" bg-blue-100 w-[100%] h-[100vh] pt-20">
    <div class="bg-white w-[45%] h-[55px] flex mx-auto shadow-xl rounded-xl pr-0 mb-[20px] border border-gray-300 max-lg:w-[85%] max-lg:mt-[80px] max-lg:mb-[120px]"> 
    <button onClick={dropdown} className="flex items-center justify-between p-4 w-[170px] bg-gray-100 border text-gray-900 rounded-l-xl mr-4">
      <p>Categories</p>
      <i class="fa-solid fa-angle-down mt-1 text-gray-400"></i>
    </button>
    <input class="w-[93%] focus:outline-none mr-2" type="text" placeholder="Search Projects..."/>
      <div className="bg-black w-[40px] h-[48x] p-3 px-7 flex items-center justify-center rounded-r-xl">
          <i class="fa fa-search text-white" aria-hidden="true"></i>
      </div>
    </div>

    {
    isclicked ?  <ul className="absolute left-[25.5%] p-5 pl-5 pr-12 rounded-xl shadow-xl bg-white">
      <li className="custom_font pb-2.5">Web Development</li>
      <li className="custom_font pb-2.5">Data Mining</li>
      <li className="custom_font pb-2.5">Blockchain</li>
      <li className="custom_font">Data Science</li>
    </ul> 
    : <ul className="hidden left-[25.5%] p-5 pl-5 pr-12 rounded-xl shadow-xl bg-white">
      <li className="custom_font pb-2.5">Web Development</li>
      <li className="custom_font pb-2.5">Data Mining</li>
      <li className="custom_font pb-2.5">Blockchain</li>
      <li className="custom_font">Data Science</li>
    </ul>
    }

    </div>;
    
}
